import { useState } from 'react';
import './NewExpense.css';
import ExpenseForm from './ExpenseForm';

const NewExpense = (props) => {
    const [showForm, setShowForm] = useState(false);

    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        };

        props.onAddExpense(expenseData);
    };

    const updateForm = () => {
        setShowForm(!showForm);
    }

    let message = <button className="new-expense__button" onClick={updateForm}>New Expense</button>

    if (showForm) {
        message = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} onUpdateForm={updateForm}/>
    } else {
        message = <button className="new-expense__button" onClick={updateForm}>New Expense</button>
    }

    return (
        <div className="new-expense">
            {message}
        </div>
    );
};

export default NewExpense;
