import { useState } from 'react';
import './NewExpense.css';
import ExpenseForm from './ExpenseForm';

const NewExpense = (props) => {
    const [isEditing, setIsEditing] = useState(false);

    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        };

        props.onAddExpense(expenseData);
    };

    const startEditingHandler = () => {
        setIsEditing(true);
    }

    let message = <button className="new-expense__button" onClick={startEditingHandler}>New Expense</button>

    if (isEditing) {
        message = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} onUpdateForm={startEditingHandler}/>
    } else {
        message = <button className="new-expense__button" onClick={startEditingHandler}>New Expense</button>
    }

    return (
        <div className="new-expense">
            {!isEditing && <button className="new-expense__button" onClick={startEditingHandler}>New Expense</button>}
            {isEditing && <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} onUpdateForm={startEditingHandler}/>}
        </div>
    );
};

export default NewExpense;
